import random
import requests

from config import proxies

# 获得代理函数
def get_proxy():
    return proxies

# 爬虫的基础类
class crawaller_base():
    def __init__(self):
        self.index_url = ""
        self.timeout = 5
        self.headers = {
            'User-Agent': UserAgent_Base().random()
        }
        self.proxy_support = None
        self.proxy = get_proxy() if self.proxy_support else {}

    # 请求函数 默认get 有其他可以重写
    def crawls_getresponse(self):
        res = requests.get(url=self.index_url,headers=self.headers,proxies=self.proxy,timeout=self.timeout)
        return res
    # 解析
    def crawls_parse(self):
        pass
    # 最终统一调用的函数
    def crawls_run(self):
        return self.crawls_parse()
    
